CREATE TABLE pages (	id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	parent_id INTEGER NOT NULL,	alias TEXT NOT NULL,	fullpath TEXT,	position INTEGER NOT NULL default '0',	status INTEGER NOT NULL default '2',	layout TEXT NOT NULL,	type TEXT NOT NULL);CREATE TABLE pages_content (	page_id INTEGER NOT NULL,	locale TEXT NOT NULL,	title TEXT default NULL,	content TEXT,	UNIQUE (page_id,locale));CREATE TABLE pages_meta (	id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	page_id INTEGER NOT NULL,	key TEXT,	value TEXT,	UNIQUE (page_id,key));CREATE TABLE config_meta (	id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	page_id INTEGER NOT NULL,	key TEXT,	value TEXT,	UNIQUE (page_id,key));