CREATE TABLE pages (	id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	parent_id int(11) NOT NULL,	alias varchar(150) NOT NULL,	fullpath TEXT,	status tinyint(1) NOT NULL default '1',	template varchar(100) NOT NULL,	type varchar(50) NOT NULL);CREATE TABLE pages_content (	page_id int(11) NOT NULL,	locale varchar(50) NOT NULL,	title varchar(500) default NULL,	content TEXT,	UNIQUE (page_id,locale));CREATE TABLE pages_meta (	id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	page_id int(11) NOT NULL,	key TEXT,	value TEXT,	UNIQUE (page_id,key));CREATE TABLE page_types (	pos INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	type varchar(50) NOT NULL,	name varchar(255) NOT NULL,	dflt_tpl varchar(100) NOT NULL,	UNIQUE (type,name));CREATE TABLE config_meta (	id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	page_id int(11) NOT NULL,	key TEXT,	value TEXT,	UNIQUE (page_id,key));CREATE TABLE access (	id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	date date NOT NULL,	ip TEXT default NULL,	login TEXT default NULL,	password TEXT default NULL,	text TEXT default NULL)